import axios from 'axios';
import LayoutDiv from '../components/LayoutDiv.vue';
import Swal from 'sweetalert2';
export default {
  name: 'ProjectEdit',
  components: {
    LayoutDiv
  },
  data() {
    return {
      project: {
        name: '',
        description: ''
      },
      isSaving: false
    };
  },
  created() {
    const id = this.$route.params.id;
    axios.get(`/api/projects/${id}`).then(response => {
      let projectInfo = response.data;
      this.project.name = projectInfo.name;
      this.project.description = projectInfo.description;
      return response;
    }).catch(error => {
      Swal.fire({
        icon: 'error',
        title: 'An Error Occured!',
        showConfirmButton: false,
        timer: 1500
      });
      return error;
    });
  },
  methods: {
    handleSave() {
      this.isSaving = true;
      const id = this.$route.params.id;
      axios.patch(`/api/projects/${id}`, this.project).then(response => {
        Swal.fire({
          icon: 'success',
          title: 'Project updated successfully!',
          showConfirmButton: false,
          timer: 1500
        });
        this.isSaving = false;
        this.project.name = "";
        this.project.description = "";
        return response;
      }).catch(error => {
        this.isSaving = false;
        Swal.fire({
          icon: 'error',
          title: 'An Error Occured!',
          showConfirmButton: false,
          timer: 1500
        });
        return error;
      });
    }
  }
};
