import axios from 'axios';
import LayoutDiv from '../components/LayoutDiv.vue';
import Swal from 'sweetalert2';
export default {
  name: 'RecipeCreate',
  components: {
    LayoutDiv
  },
  data() {
    return {
      recipe: {
        name: '',
        preparation: '',
        time: '',
        serve: '',
        difficulty: '',
        ingredients: [{}]
      },
      isSaving: false
    };
  },
  methods: {
    handleSave() {
      this.isSaving = true;
      axios.post('/recipes', this.recipe).then(response => {
        Swal.fire({
          icon: 'success',
          title: 'Recipe saved successfully!',
          showConfirmButton: false,
          timer: 1500
        });
        this.isSaving = false;
        this.recipe.name = "";
        this.recipe.preparation = "";
        this.recipe.time = "";
        this.recipe.serve = "";
        this.recipe.difficulty = "";
        return response;
      }).catch(error => {
        this.isSaving = false;
        Swal.fire({
          icon: 'error',
          title: 'An Error Occured!',
          showConfirmButton: false,
          timer: 1500
        });
        return error;
      });
    }
  }
};
